// Helper data classes
data class YearData(
    val temperatures: Map<Int, YearDetail>
)

data class YearDetail(
    val temp: Double,
    val overlays: Overlays,
    val text: String
)

data class Overlays(
    val pictures: List<Int>,
    val websites: List<Int>
)

data class Measure(
    val id: Int,
    val name: String,
    val startYear: Int,
    val tempChange: Double,
    val info: Int,
    val upgrades: List<Int> = emptyList() // List of measure IDs this measure upgrades to or replaces
)

